import React from "react";
import { Route, Routes, Navigate } from "react-router-dom";
import { Header } from "./components/Header";
import { Navbar } from "./components/Navbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import IssueRegister from "./pages/IssueRegister";
import Stats from "./pages/Stats";
import MyIssues from "./pages/MyIssues";
import Admin from "./pages/Admin";

function App() {
  const isLoggedIn = false; // 로그인 상태 여부를 설정하세요.

  return (
    <div className="min-h-screen relative flex flex-col">
      {/* 로그인 상태에 따라 헤더를 표시하거나 숨깁니다 */}
      {isLoggedIn && <Header />}
      <div className="flex flex-grow mt-16">
        {/* 로그인 상태에 따라 네비게이션을 표시하거나 숨깁니다 */}
        {isLoggedIn && <Navbar />}
        <div className="flex-grow p-4 ml-60">
          <Routes>
            <Route path="/" element={<Home />} />
            {/* 로그인 상태에 따라 로그인 페이지로 리다이렉트합니다 */}
            {!isLoggedIn && <Route path="/login" element={<Login />} />}
            {!isLoggedIn && <Route path="/signup" element={<Signup />} />}
            {/* 로그인 상태에 따라 다른 페이지로 리다이렉트합니다 */}
            {isLoggedIn ? (
              <>
                <Route path="/issue-register" element={<IssueRegister />} />
                <Route path="/stats" element={<Stats />} />
                <Route path="/my-issues" element={<MyIssues />} />
                <Route path="/admin" element={<Admin />} />
              </>
            ) : (
              <Navigate to="/login" />
            )}
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
