// Header.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logoImage from '../assets/images/logo.png';
import { useHistory } from 'react-router-dom';

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false); // 드롭다운 메뉴 표시 여부

  // 히스토리 객체 생성
  const history = useHistory();

  // 로그아웃 함수
  const handleLogout = () => {
    setIsLoggedIn(false);
    // 로그아웃 후 로그인 페이지로 이동
    history.push('/login');
  };

  // 드롭다운 메뉴 토글 함수
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <header className="w-full h-20 bg-gray-800 text-white flex items-center justify-between px-4 fixed top-0 left-0 z-10">
      <div className="flex items-center">
        <img src={logoImage} alt="logo" className="h-24 pl-6" />
      </div>
      {isLoggedIn ? (
        <div className="relative">
          <img
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQxUjuMp6J34mOz5Mof17-YzNcl9FkUPAv57pi247dndA&s"
            alt="User Avatar"
            className="w-10 h-10 rounded-full cursor-pointer"
            onClick={toggleDropdown}
          />
          {showDropdown && (
            <div className="absolute right-0 mt-3 w-40 bg-white text-black py-2 px-4 rounded shadow">
              <div>웨이드</div>
              <div>wade@cau.cac.kr</div>
              <hr className="my-2" />
              <button className="text-red-500 hover:text-red-700" onClick={handleLogout}>
                로그아웃
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className="flex items-center space-x-4">
          <Link to="/login" className="text-white hover:text-gray-300">
            로그인
          </Link>
          <Link to="/signup" className="text-white hover:text-gray-300">
            회원가입
          </Link>
        </div>
      )}
    </header>
  );
};

export default Header;
