import { Link, useLocation } from "react-router-dom";

export const Navbar = () => {
  const location = useLocation();

  // 네비게이션 바 클릭 시 해당 페이지 파란색 버튼 생성
  const getNavLinkClass = (path) => {
    return location.pathname === path 
      ? "text-black bg-gray-800 rounded-lg p-2" 
      : "text-black hover:text-blue-500 text-white";
  };

  return (
    <nav className="fixed left-0 top-16 h-full w-60 border-r border-black flex flex-col justify-start pt-4">
      <div className="flex flex-col gap-8 items-center">
        <Link to="/" className={getNavLinkClass("/")}>Home</Link>
        <Link to="/login" className={getNavLinkClass("/login")}>Login</Link>
        <Link to="/signup" className={getNavLinkClass("/signup")}>Signup</Link>
      </div>
    </nav>
  );
};
