import React, { useState, useEffect } from 'react';

const IssueDetails = () => {
  const [issue, setIssue] = useState(null);

  useEffect(() => {
    // API를 호출하여 이슈 데이터를 가져옵니다.
    const fetchIssue = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/issues/1');
        if (response.ok) {
          const data = await response.json();
          setIssue(data);
        } else {
          console.error('이슈 데이터를 불러오는 데 실패했습니다.');
        }
      } catch (error) {
        console.error('API 호출 중 오류가 발생했습니다:', error);
      }
    };

    fetchIssue();
  }, []);

  if (!issue) {
    return <div>Loading...</div>;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:8080/api/issues/${issue.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(issue),
      });
      if (response.ok) {
        console.log('이슈가 성공적으로 수정되었습니다.');
      } else {
        console.error('이슈 수정에 실패했습니다.');
      }
    } catch (error) {
      console.error('API 호출 중 오류가 발생했습니다:', error);
    }
  };

  return (
    <div className="grid gap-5 p-5 bg-white w-4/5 mx-auto">
      <h2 className="text-2xl font-bold mb-2">이슈 확인 및 수정</h2>
      <div className="bg-[#DDF1FF] p-5 rounded-lg shadow-md">
        <h2 className="text-l font-semibold">#{issue.id} {issue.title}</h2>
        <div className="h-[1px] bg-gray-800 my-4"></div>
        <div className="grid grid-cols-3 gap-2 mb-4">
          <div>Reporter: {issue.reporter}</div>
          <div>Reported Date: {issue.reportedDate}</div>
          <div>이슈 상태: {issue.status}</div>
        </div>
        <div className="grid grid-cols-3 gap-2 mb-4">
          <div>우선 순위: {issue.priority}</div>
          <div>담당자: {issue.assignee}</div>
          <div></div>
        </div>
        <div className="h-[1px] bg-gray-800 my-4"></div>
        <p className="text-gray-700">
          {issue.description}
        </p>
      </div>

      <h2 className="text-2xl font-semibold">이슈 수정</h2>
      <form onSubmit={handleSubmit} className="bg-[#DDF1FF] p-5 rounded-lg shadow-md">
        <div className="mb-5">
          <div>
            <input type="text" value={issue.title} className="w-full p-2 mb-2 border rounded" readOnly />
            <div className="mt-4">
              <textarea value={issue.description} className="w-full p-2 h-36 border rounded"></textarea>
            </div>
          </div>
        </div>
        <div className="mb-5 flex items-center">
          <label className="block mb-1 font-semibold mr-2">우선 순위</label>
          <select value={issue.priority} className="p-2 border rounded w-1/5">
            <option value="blocker">blocker</option>
            <option value="critical">critical</option>
            <option value="major">major</option>
            <option value="minor">minor</option>
            <option value="trivial">trivial</option>
          </select>
        </div>
        <div className="mb-5 flex items-center">
          <label className="block mb-1 font-semibold mr-2">담당자</label>
          <select value={issue.assignee} className="p-2 border rounded w-1/5">
            <option>담당자1</option>
            <option>담당자2</option>
            <option>담당자3</option>
          </select>
        </div>
        <div className="mb-5">
          <label className="block mb-1 font-semibold">이슈 상태:</label>
          <div className="flex space-x-4 mt-2">
            <label><input type="radio" name="status" value="new" className="mr-2" /> NEW</label>
            <label><input type="radio" name="status" value="assigned" className="mr-2" /> ASSIGNED</label>
            <label><input type="radio" name="status" value="fixed" className="mr-2" /> FIXED</label>
            <label><input type="radio" name="status" value="resolved" className="mr-2" /> RESOLVED</label>
            <label><input type="radio" name="status" value="closed" className="mr-2" /> CLOSED</label>
          </div>
        </div>
        <button type="submit" className="w-32 bg-blue-900 text-white rounded p-2">변경 사항 적용하기</button>
      </form>

      <h2 className="text-2xl font-semibold">Comment</h2>
      <div className="bg-[#DDF1FF] p-5 rounded-lg shadow-md">
        <div className="mb-5">
          <div>
            <input type="text" placeholder="이슈 제목(Incorporate footnotes into Trac)" className="w-full p-2 mb-2 border rounded" />
            <div className="mt-4">
              <textarea placeholder="수정할 내용을 적어주세요" className="w-full p-2 h-36 border rounded"></textarea>
            </div>
            <button className="w-32 mt-4 p-2 bg-blue-900 text-white rounded">코멘트 등록</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IssueDetails;
