import React, { useState, useEffect } from 'react';

export const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userImage, setUserImage] = useState('');

  useEffect(() => {
    // 이 부분은 실제 로그인 상태와 사용자 이미지를 가져오는 API 호출로 대체해야 합니다.
    // 예시로 더미 데이터를 사용합니다.
    const fetchUserData = async () => {
      // 예시로 1초 후에 로그인 상태와 사용자 이미지 업데이트
      setTimeout(() => {
        setIsLoggedIn(true);
        setUserImage('https://via.placeholder.com/40'); // 사용자 이미지 URL
      }, 1000);
    };

    fetchUserData();
  }, []);

  return (
    <header className="w-full h-16 bg-gray-800 text-white flex items-center justify-between px-4 fixed top-0 left-0 z-10">
      <h1>OH자일 이슈 관리 시스템</h1>
      {isLoggedIn && (
        <div className="flex items-center">
          <img
            src={userImage}
            alt="User Avatar"
            className="w-10 h-10 rounded-full"
          />
        </div>
      )}
    </header>
  );
};
