import { Link, useLocation } from "react-router-dom";

export const Navbar = () => {
  const location = useLocation();

  // 네비게이션 바 클릭 시 해당 페이지 파란색 버튼 생성
  const getNavLinkClass = (path) => {
    return location.pathname === path 
      ? "text-white bg-gray-800 rounded-lg p-2 w-50 flex items-center"
      : "text-black hover:text-blue-500 flex items-center";
  };

  // 현재 페이지에 해당하는 경우 적용할 스타일
  const activeLinkStyle = {
    width: "50px",
    display: "flex",
    alignItems: "center",
  };

  // 조그만 가로 막대 스타일
  const barStyle = {
    width: "5px",
    height: "100%",
    backgroundColor: "gray",
    borderRadius: "5px 0 0 5px",
    marginRight: "10px",
  };

  return (
    <nav className="fixed left-0 top-16 h-full w-60 border-r border-black flex flex-col justify-start pt-4">
      <div className="flex flex-col gap-8 items-start">
        <div className="flex items-center">
          {location.pathname === "/" && <div style={barStyle}></div>}
          <Link to="/" className={getNavLinkClass("/")} style={location.pathname === "/" ? activeLinkStyle : {}}>
            Home
          </Link>
        </div>
        <div className="flex items-center">
          {location.pathname === "/login" && <div style={barStyle}></div>}
          <Link to="/login" className={getNavLinkClass("/login")} style={location.pathname === "/login" ? activeLinkStyle : {}}>
            Login
          </Link>
        </div>
        <div className="flex items-center">
          {location.pathname === "/signup" && <div style={barStyle}></div>}
          <Link to="/signup" className={getNavLinkClass("/signup")} style={location.pathname === "/signup" ? activeLinkStyle : {}}>
            Signup
          </Link>
        </div>
      </div>
    </nav>
  );
};
